(window.webpackJsonp=window.webpackJsonp||[]).push([[55],{491:function(v,_,p){"use strict";p.r(_);var n=p(0),t=Object(n.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("div",{staticClass:"custom-block warning"},[_("p",{staticClass:"custom-block-title"},[v._v("树洞随笔")]),v._v(" "),_("p",[v._v("知乎转载")])]),v._v(" "),_("p",[v._v("（一）\n小高六岁开始学剑。")]),v._v(" "),_("p",[v._v("那年小高他妈忙着复习考大学，他爸忙着南北往返倒彩电。")]),v._v(" "),_("p",[v._v("六岁的小高——那时候还是小小高——自己在屋里玩，不知怎么从床缝里摸出了他爷爷留下的一把汉剑。")]),v._v(" "),_("p",[v._v("剑长三尺三寸，紫檀为鞘，八面研磨，铜首玉镡。")]),v._v(" "),_("p",[v._v("小高不知道哪来的力气，硬是把这玩意从鞘里拽了出来。他妈妈中午回家做饭，看见了自己这辈子都忘不了的惊心动魄一幕：家里一片狼藉，到处是劈砍的痕迹，自己结婚时使了大人情，请人打出来的樟木大衣柜支离破碎，毛料呢子撒的满地都是。\n小高妈差点背过气去，疯了似的跑进屋里找孩子。结果看见小高正在那拎着剑做 金鸡独立 式，一颗悬着的心总算放了下来——紧跟着上来的，是冲天的怒火。")]),v._v(" "),_("p",[v._v("家里的鸡毛掸子硬是被打折了两把。")]),v._v(" "),_("p",[v._v("可无论怎么打，小高就是抱着那把剑不撒手。家里大人打也打了，骂也骂了，小高岿然不动，只要他的剑。")]),v._v(" "),_("p",[v._v("小高的爸爸回家后知道这事，半晌无语，后来叹了口气，说这都是命。")]),v._v(" "),_("p",[v._v("于是6岁那年，小高开始正式跟着师傅学剑。")]),v._v(" "),_("p",[v._v("拜师那天金星凌日 ，主刀兵之难大兴。")]),v._v(" "),_("p",[v._v("结果就在那天，公安部颁布了一个条例。")]),v._v(" "),_("p",[v._v("《对部分刀具实行管制的暂行规定》")]),v._v(" "),_("p",[v._v("是日，金星凌日，未己，紫微星盛，诸星自晦。")]),v._v(" "),_("p",[v._v("（二）")]),v._v(" "),_("p",[v._v("小高的师傅是个老头，不过没有白胡子。")]),v._v(" "),_("p",[v._v("小高的爸爸叫他青先生，说他是爷爷的朋友。")]),v._v(" "),_("p",[v._v("青先生每日寅时教小高练剑，卯时则去，十年间雨雪不辍。")]),v._v(" "),_("p",[v._v("只是再不让小高动那把汉剑。青先生说那把剑非生死之间不可擅动，小高谨遵师诲。")]),v._v(" "),_("p",[v._v("于是这年小高十六岁。运剑如臂使指，来如雷霆收震怒，罢如江海凝清光。青先生说要是搁以前，你这是开宗立派的本事。\n小高收剑立定，剑眉朗目，风姿卓越。")]),v._v(" "),_("p",[v._v("青先生说，我再没什么可教你的了，只有一本书留给你。咱们师徒一场，就此别过。")]),v._v(" "),_("p",[v._v("小高红了眼圈，十年师徒如父子，可惜师傅要走，自己留不住。于是小高恭恭敬敬的给青先生磕了三个头，收下了放着那本书的匣子。")]),v._v(" "),_("p",[v._v("回到家中，小高打开匣子，一本《刑法》。")]),v._v(" "),_("p",[v._v("是夜小高大醉，抱着那把汉剑哭了一宿。")]),v._v(" "),_("p",[v._v("（三）")]),v._v(" "),_("p",[v._v("小高他妈担心小高，第二天一大早给小高送 醒酒汤 。不料推门时发现屋中被褥整整齐齐，房间里哪还有小高的踪迹。")]),v._v(" "),_("p",[v._v("小高父母大惊失色——这孩子莫不是想不开离家出走了？正慌乱时，却见小高一身白衣从外面施施然回家，原来是早起背英语单词去了。")]),v._v(" "),_("p",[v._v("小高虽然回来了，可父母心里依然没底。这孩子练了十年的剑，忽然就绝口不提练剑的事，开始发奋图强、努力学习，不会是打击太大受了刺激吧？结果一连几个月，小高学习成绩扶摇直上，真的再也不提练剑的事。父母心中稍定，随之而来的，是大大的欣慰。")]),v._v(" "),_("p",[v._v("只是之后家里再没人见过那把汉剑。倒是师傅留下的那本书被装到匣子里，恭恭敬敬的被收了起来。父母问的时候，小高就只是笑笑，说青先生这是怕他少年血气未定、好狠斗勇，留下来时时提醒自己的。")]),v._v(" "),_("p",[v._v("之后数年，小高一路精进，学业有成，去了天子脚下国人耳熟能详的那所大学。选专业时小高力排众议，去学了法律。\n小高他爸死活不干，拿着一份报纸跟小高拍了桌子，说21世纪生物必大行其道，学生物前途无量。小高笑笑，自顾自地在志愿书上写下“法律系”三个大字，小高爸妈 如丧考妣 ，说这孩子怕是魔障了，以后当个讼棍，只要叫街坊笑话。只有小高的爷爷若有所思，问小高为什么非学法律不可。")]),v._v(" "),_("p",[v._v("小高说了八个字。\n除疑定法，咸知所辟。")]),v._v(" "),_("p",[v._v("（四）")]),v._v(" "),_("p",[v._v("四年之后，小高学业大成，众多律师大所纷纷邀请加盟。结果被小高一一回绝，回到家里开了家小工作室。")]),v._v(" "),_("p",[v._v("小高给工作室取了个名字，叫做“鲜灼”。汉隶书就的牌匾挂在一片“沙县小吃”、“强子发艺”的杀马特招牌之间，显得格格不入。")]),v._v(" "),_("p",[v._v("工作室开张大半年，一单生意也没接到。小高倒是不着急，找了一份企业法务的兼职养活自己。旁边一片商铺里的小老板们与小高混得渐渐熟络，偶尔也取笑一下小高，问一些“高律师，你当真是X大毕业的大律师么？”之类的话。小高也乐得配合，摆出一副不屑置辩的神情，回几句刑法第266条有三种量刑，你们可晓得一类的话，大家伙哈哈一笑，便又是一天过去了。\n只有一次，小高喝大了。又哭又笑，说你们知不知道，我学法就是为了搞清楚什么能干，什么不能干。结果学了四年，才知道这他妈的世道我什么都不能干！我什么都干不了！然后在众人惊愕的眼神中跳上桌子仰天长啸，说老子要做侠客啊，侠客啊！\n然后一个金鸡独立，摔成了一个大花脸。")]),v._v(" "),_("p",[v._v("从此江湖传言，高律师人哪都好，唯一要注意的，就是千万别让他喝高了。")]),v._v(" "),_("p",[v._v("（五）")]),v._v(" "),_("p",[v._v("这天中午小高照例跑到旁边“沙县小吃”来吃午饭，进门却只见一地狼藉，四五个汉子正把老板围在中间，一个孕妇坐在地上，满嘴是血，正在干嚎。")]),v._v(" "),_("p",[v._v("小高一愣：这是怎么了？")]),v._v(" "),_("p",[v._v("“沙县小吃”的老板见到小高犹如见到亲人，赶紧从人群中挤冲出来，拽住小高的袖子让他来给评理。")]),v._v(" "),_("p",[v._v("原来这伙人在附近转悠六七天了，专门挑饭口时间到各个小店里吃饭，吃到一半就把玻璃碴、钉子往孕妇嘴里一塞，说是店里的东西不干净，让孕妇受了惊，非要个十万块的赔偿不可。")]),v._v(" "),_("p",[v._v("小高一咧嘴——这破店统共才值几个钱，十万块？扯淡了。于是走上前去，自亮身份，便想说大家给个面子，互相退让一步，调解一下算了。")]),v._v(" "),_("p",[v._v("结果为首的汉子听说小高要趟这摊浑水，二话没说就是一个巴掌，巴掌落在小高细皮嫩肉的脸上，漾出一道红斑，响声清脆。\n汉子洋洋得意，说你们这些讼棍就是他妈欠揍。")]),v._v(" "),_("p",[v._v("小高咬咬牙，说你们这是严重违反行政治安管理处罚条例，要是再闹下去，就不好收场了。")]),v._v(" "),_("p",[v._v("收场？那汉子哈哈大笑，说老子带着兄弟们混了这么多年，就没想过收场！放心，爷爷打你一顿，保证你连个油皮都破不了，到时候进了局子蹲七天就得！等爷爷出来了再收拾你一顿，看看是他妈你懂法，还是爷爷我会用法！")]),v._v(" "),_("p",[v._v("说着抡圆了巴掌，又是一记耳光。")]),v._v(" "),_("p",[v._v("这次巴掌在半道被小高截住了。小高阴沉着脸，连连摇头，眼中满是懊恼：我真笨啊，真笨！连他妈几个痞子都懂的道理，我竟然这么些年都没想明白！")]),v._v(" "),_("p",[v._v("小高掰着那大汉的胳膊，一点点地把它扭到一个奇异的角度，冲着大汉露出一个微笑道：不用叫那么大声，我有分寸。然后扫了一眼几个逼上来的汉子，笑道：你们知道，鲜灼是什么意思么？")]),v._v(" "),_("p",[v._v("我灼你妈！一个汉子抡着折椅，扑了上来。")]),v._v(" "),_("p",[v._v("小高轻巧地避开折椅，捡起了一根筷子：火形严，故人鲜灼。我以前只觉得刑法严峻，持剑当避，今天碰上你们几个，才他妈明白——")]),v._v(" "),_("p",[v._v("老子也可以把你们这种东西架在火上烤啊！")]),v._v(" "),_("p",[v._v("握着筷子的小高，衣袂无风自动，像极了一位剑客。")]),v._v(" "),_("p",[v._v("（六）")]),v._v(" "),_("p",[v._v("小高是跟一地鼻青脸肿的汉子们一起被带回警察局的。老警察对小高颇不耐烦，然而当他看到被小高打趴下的一群汉子们时，眼神还是变了变。")]),v._v(" "),_("p",[v._v("看不出来，你小子还挺能打，学散打的？")]),v._v(" "),_("p",[v._v("小高笑笑：学法律的，X大法学院毕业。")]),v._v(" "),_("p",[v._v("老警察嗤笑一声， 知法犯法 啊？说说吧，怎么个情况？")]),v._v(" "),_("p",[v._v("没什么情况，他们来闹事，敲诈店主， 寻衅滋事 。我见义勇为，在这过程中进行了合理的正当防卫，造成了一点轻微伤和轻伤。我申请对双方身上的伤势进行司法鉴定。")]),v._v(" "),_("p",[v._v("老警察瞪着眼看着小高：门儿清啊小子。")]),v._v(" "),_("p",[v._v("门外一个小警官风一样地跑了进来，拉住了老警察。")]),v._v(" "),_("p",[v._v("怎么了？老警察问。")]),v._v(" "),_("p",[v._v("这人……太他妈邪乎了。小警官一脸惊恐地看着小高，满眼的不敢置信：他才是轻伤的那个。")]),v._v(" "),_("p",[v._v("老警察差点让口水呛死，一把把小警官扒拉到旁边说，你是不是疯了？外面倒了一地的人在那哼哼唧唧，你跟我说坐着的这个是轻伤？那外面的那些算什么？重伤？")]),v._v(" "),_("p",[v._v("不。")]),v._v(" "),_("p",[v._v("小警官努力平息了一下自己的心情：外面那些，好多连轻微伤都算不上。")]),v._v(" "),_("p",[v._v("老警察不信，连夜联系了公安医院，把这些人连同小高一起送过去验伤。结果没用一天，检验结果就出来了。")]),v._v(" "),_("p",[v._v("外面那些人，大部分都不够轻微伤标准。虽然看着鼻青脸肿的，但脸上划伤长度被很严谨的控制在了两厘米以内，瘀伤面积也不够，验伤的医生啧啧称奇，说这怕不是拿尺子量着打的吧。")]),v._v(" "),_("p",[v._v("而小高腹膜之间有半块厨刀碎片，跟现场的一把厨刀恰好吻合，那厨刀上有闹事汉子的指纹，铁证如山。法医感慨说小高运气真好，这玩意再往里两分、或是上下移动两分怕都得要命，偏偏断在腹膜之间，做个小手术取出来就好。小高连连点头，说是自己命大。")]),v._v(" "),_("p",[v._v("然而运气再好，这也是不折不扣的轻伤。老警察一脸见鬼的回到了所里，咬牙切齿地撕了自己的出警报告，重新写了一份。上面很快给了答复：小高属于正当防卫，未超出必要限度，来闹事的几个汉子按寻衅滋事和故意伤害罪被起诉。")]),v._v(" "),_("p",[v._v("小高在医院住了七天，接受了记者采访，又获得了政府的十万块见义勇为奖励。捧着奖状的小高出院后仰天长啸，意气风发。")]),v._v(" "),_("p",[v._v("（七）")]),v._v(" "),_("p",[v._v("小高出名了。")]),v._v(" "),_("p",[v._v("大家现在都知道，有个高律师，专治疑难杂症。")]),v._v(" "),_("p",[v._v("别的律师管不了的案子，什么强拆讨债、民族纠纷、欺行霸市，他都能管。")]),v._v(" "),_("p",[v._v("而最厉害的是，这位高律师，是从来不要律师费的。")]),v._v(" "),_("p",[v._v("他只要医药费。")]),v._v(" "),_("p",[v._v("谁闹事，就管谁要。")]),v._v(" "),_("p",[v._v("城东的大地癞子王二不信邪，说他妈一个小崽子，还能翻了天去？结果第二天带着人收账的时候正好碰上高律师，王二嘴上叫得厉害，私底下的心思却活络的很，早就让手下人准备了一桶大粪。就等着往高律师身上泼屎——难不成你还能把这玩意反弹回来？")]),v._v(" "),_("p",[v._v("结果手下抬着桶莫名其妙的摔了一地，大桶砸断了王二的右腿，溢出来的黄白之物把王二沒在其中，差点把他闷死。\n几个警察接到报警赶到现场时，王二正在粪堆里嚎丧。小高站在旁边，一身白衣如雪，手里拎着支钢笔，见到警察时热情洋溢：警察同志，您可算来了，他们把我打的啊，我估计至少得是个轻微伤。")]),v._v(" "),_("p",[v._v("几个警察强忍着胃里的翻江倒海：高律师，你可也差不多一点。这人都让你整成这样了，挨揍的还是你？")]),v._v(" "),_("p",[v._v("怎么，不信我？小高一挑眉毛，说我可是带着记录仪的，咱们拿证据说话！不信你问问他们也行！说着一努嘴，地上的王二吓得一个哆嗦，翻滚着爬向一个警察，喊着是我是我，你们赶紧把我带走吧。就要上去抱警察的大腿。")]),v._v(" "),_("p",[v._v("几个警察到底再没忍住，吐了一地。")]),v._v(" "),_("p",[v._v("市里的法医倒了血霉，四个月被投诉了七次，市局领导亲自过问，说下面闹得沸沸扬扬，怀疑你们收了那个高律师的贿赂。怎么每次都是别人鼻青脸肿，他安然无恙，最后一鉴定反而是别人要给他赔医药费？法医说那货出手准的惊人，说给你打成什么伤就打成什么伤，偏偏每次被别人打的时候都伤得很重，却一点都不要命，我们能有什么办法！")]),v._v(" "),_("p",[v._v("市里的流氓小偷地癞子纷纷 金盆洗手 ，一时间本市的治安前所未有的好。")]),v._v(" "),_("p",[v._v("（尾声）")]),v._v(" "),_("p",[v._v("一晃又是数年过去，高律师功成名就，娶妻生子。妻子温婉美丽，儿子顽皮可爱，人人羡慕。")]),v._v(" "),_("p",[v._v("一天高律师正在书房翻阅卷宗，六岁的儿子噔噔噔的从外面跑进来，捧着一个盒子问他里面装的是什么。原来正是装老师当年给小高留下的那本书的盒子。小高心中感慨，手上慢了一步，儿子却已经将盒子打开了。")]),v._v(" "),_("p",[v._v("一道剑气激荡！")]),v._v(" "),_("p",[v._v("高律师心里一惊——当年自己骤然受挫，将书收到盒中，不料老师竟然在书页上留下了一道剑气！这些年自己从未打开过盒子，自然是无从发现。然而再想拦时已经来不及了。高律师一个箭步蹿过去，却见儿子在漫天飞舞的纸屑里正笑得欢，总算是放下了心。")]),v._v(" "),_("p",[v._v("此时盒中之书已经尽皆化为碎屑，只有一张纸，端端正正的留在盒底，上面五个大字，正是老师当年亲笔所书。\n侠以武犯禁。")])])}),[],!1,null,null,null);_.default=t.exports}}]);